CREATE TABLE "users" (
  "id" INT GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,
  "name" varchar(30) NOT NULL,
  "code" varchar(5) UNIQUE,
  "email" varchar(50) NOT NULL,
  "password" varchar(15) NOT NULL,
  "age" int NOT NULL
);

CREATE TABLE "categories" (
  "id" INT GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,
  "name" varchar(50) NOT NULL
);

CREATE TABLE "videos" (
  "id" INT GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,
  "title" varchar(30) NOT NULL,
  "url" varchar(120) NOT NULL
);

CREATE TABLE "courses" (
  "id" INT GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,
  "title" varchar(30) NOT NULL,
  "description" varchar(50) NOT NULL,
  "level" varchar(20) NOT NULL,
  "teacher_id" int NOT NULL,
  "video" varchar(20) NOT NULL
);

CREATE TABLE "categories_courses" (
  "category_id" int NOT NULL,
  "course_id" int NOT NULL
);

CREATE TABLE "roles" (
  "rol_id" int PRIMARY KEY NOT NULL,
  "type" varchar(10) NOT NULL
);

CREATE TABLE "videos_courses" (
  "video_id" int UNIQUE NOT NULL,
  "title" varchar(20) NOT NULL,
  "url" varchar(120) NOT NULL,
  "course_id" int NOT NULL
);

ALTER TABLE "courses" ADD FOREIGN KEY ("teacher_id") REFERENCES "users" ("id");

ALTER TABLE "categories_courses" ADD FOREIGN KEY ("course_id") REFERENCES "courses" ("id");

ALTER TABLE "categories_courses" ADD FOREIGN KEY ("category_id") REFERENCES "categories" ("id");

ALTER TABLE "videos_courses" ADD FOREIGN KEY ("course_id") REFERENCES "courses" ("id");

ALTER TABLE "videos_courses" ADD FOREIGN KEY ("video_id") REFERENCES "videos" ("id");

ALTER TABLE "roles" ADD FOREIGN KEY ("rol_id") REFERENCES "users" ("id");

